glsl3.vert:
precision highp float;
layout(std140) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
  mediump vec4 cc_probeInfo;
  mediump vec4 cc_debug_view_mode;
};
layout(std140) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_surfaceTransform;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
  mediump vec4 cc_nearFar;
  mediump vec4 cc_viewPort;
};
in vec3 a_position;
in vec2 a_texCoord;
in vec4 a_color;
out vec4 color;
out vec4 v_light;
out vec2 uv0;
vec4 vert () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  uv0 = a_texCoord;
  color = a_color;
  v_light = a_color;
  return pos;
}
void main() { gl_Position = vert(); }

glsl3.frag:
precision highp float;
float rand(vec2 co) {
  return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}
const vec3 FLASH = vec3(1.);
const vec3 VENO = vec3(0.2,0.96,0.2);
const vec3 FROST = vec3(0.3,0.2,0.75);
const vec3 THUNDER = vec3(0.8,0.95,0.1);
const vec3 weight = vec3(0.2126, 0.7152, 0.0722);
const vec3 bw = vec3(0.3, 0.6, 0.1);
vec4 Blackwhite(in vec4 color){
  vec3 final = vec3(dot(bw, color.rgb));
  return  vec4(final, color.a);
}
vec4 Inverted(in vec4 color){
  vec3 final = vec3(1.) - color.rgb;
  return vec4(final, color.a);
}
const mat3 vintage = mat3(0.393, 0.769, 0.189,
                          0.349, 0.686, 0.168,
                          0.272, 0.534, 0.131);
vec4 WindTransition (vec4 color, vec4 mixColor, float dir, float progression, float tranSize) {
  float r = rand(vec2(0, dir));
  float f = dir * (1.0 - tranSize) + tranSize*r - (progression * (1.0 + tranSize));
  float m = smoothstep(0.0, - tranSize, f);
  color = mix(color, mixColor, m);
  return color;
}
in vec4 v_light;
in vec4 color;
in vec2 uv0;
uniform sampler2D cc_spriteTexture;
layout(std140) uniform Constant {
  float methods;
};
vec4 frag () {
  int methods = int(methods);
  vec4  o = texture(cc_spriteTexture, uv0);
  vec4 BW = Blackwhite(o);
  if(methods == 0) {
    o = WindTransition(vec4(1.0,1.0,1.0,0), o, uv0.y,1.0 - color.r, 0.25);
  } else if(methods == 1) {
    o = WindTransition(o, vec4(1.0,1.0,1.0,0),uv0.y,1.0 - color.r, 0.25);
  } else if(methods == 3) {
    o.rgb = mix(o.rgb, FLASH, 0.5);
  } else if(methods == 4) {
    o.rgb = mix(o.rgb, VENO * BW.rgb *1.5, v_light.g);
  } else if(methods == 5) {
    o.rgb = mix(o.rgb, FROST * BW.rgb *2.0, v_light.b);
  } else if(methods == 6) {
    vec4 IV = Inverted(BW);
    o.rgb = mix(o.rgb, THUNDER * IV.rgb*1.0 , v_light.a);
  }
  return o;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

